pages/account/orders/completed:
---
customer:
orders:
limit: 3
---
{{#partial "page"}}
<div class="account">
    {{> components/common/breadcrumbs breadcrumbs=breadcrumbs}}
    <h2 class="account-heading title">{{lang 'account.orders.heading' }}</h2>
    {{> components/account/navigation account_page='orders'}}
    <div class="account-row orders-row">
        <section class="orders">
            {{> components/account/orders-list orders=customer.orders}}
            {{> components/common/small-paginator pagination.orders}}
        </section>
    </div>
</div>
{{/partial}}
{{> layout/base}}

components/common/body:
{{#or
(if page_type '===' 'default')
}}
<div class="body">
    {{> components/common/header}}
    {{#block "hero"}} {{/block}}
    {{else}}
    <div class="body body-alt">
        {{/or}}
        {{#or
        (if page_type '===' 'default')
        (if page_type '===' 'category')
        (if page_type '===' 'product')
        (if page_type '===' 'page')
        }}
        <div class="container" id="container-alt">
            {{else}}
            <div class="container" id="container">
                {{/or}}
                {{#block "page"}} {{/block}}
            </div>
            {{> components/common/modal}}
        </div>
    </div>
</div>
