components/products/price:
{{#or has_options product.options.length}}
    {{#if price.with_tax}}
        <div class="price-section price-section--withTax" {{#if schema_org}}itemprop="offers" itemscope itemtype="http://schema.org/Offer"{{/if}}>
            {{#if price.rrp_with_tax}}
                <span data-product-rrp-with-tax class="price price--rrp">{{price.rrp_with_tax.formatted}}</span>
            {{/if}}
            {{#if page_type '!==' 'product'}}
                {{#if theme_settings.price_as_low_as}}
                    <span translate>As low as </span>
                {{/if}}
            {{/if}}
            <span data-product-price-with-tax class="price">{{price.with_tax.formatted}}</span>
            {{#if price.without_tax}}
                {{lang 'products.price_with_tax' tax_label=price.tax_label}}
            {{/if}}
        </div>
    {{/if}}
    {{#if price.without_tax}}
        <div class="price-section price-section--withoutTax {{#if price.with_tax}}price-section--minor{{/if}}"  {{#if schema_org}}itemprop="offers" itemscope itemtype="http://schema.org/Offer"{{/if}}>
            {{#if price.rrp_without_tax}}
                <span data-product-rrp-without-tax class="price price--rrp">{{price.rrp_without_tax.formatted}}</span>
            {{/if}}
            {{#if schema_org}}
                <meta itemprop="price" content="{{price.without_tax.value}}">
                <meta itemprop="priceCurrency" content="{{currency_selector.active_currency_code}}">
                <meta itemprop="availability" content="{{product.availability}}">
                <meta itemprop="itemCondition" itemtype="http://schema.org/OfferItemCondition" content="http://schema.org/{{product.condition}}Condition">
            {{/if}}
            {{#if page_type '!==' 'product'}}
                {{#if theme_settings.price_as_low_as}}
                    <span translate>As low as </span>
                {{/if}}
            {{/if}}
            <span data-product-price-without-tax class="price price--withoutTax">{{price.without_tax.formatted}}</span>
            {{#if price.with_tax}}
                <abbr title="{{lang 'products.excluding_tax'}}">{{lang 'products.price_without_tax' tax_label=price.tax_label}}</abbr>
            {{/if}}
        </div>
    {{/if}}
    {{#if page_type '===' 'product'}}
        {{#if price.saved}}
            <div class="price-section price-section--saving">
                <span class="price">
                    {{lang 'products.you_save' amount=price.saved.formatted}}
                </span>
            </div>
        {{/if}}
    {{/if}}
{{else}}
    {{#if price.with_tax}}
        <div class="price-section price-section--withTax" {{#if schema_org}}itemprop="offers" itemscope itemtype="http://schema.org/Offer"{{/if}}>
            {{#if price.rrp_with_tax}}
                <span data-product-rrp-with-tax class="price price--rrp">{{price.rrp_with_tax.formatted}}</span>
            {{/if}}
            <span data-product-price-with-tax class="price">{{price.with_tax.formatted}}</span>
            {{#if price.without_tax}}
                {{lang 'products.price_with_tax' tax_label=price.tax_label}}
            {{/if}}
        </div>
    {{/if}}
    {{#if price.without_tax}}
        <div class="price-section price-section--withoutTax {{#if price.with_tax}}price-section--minor{{/if}}"  {{#if schema_org}}itemprop="offers" itemscope itemtype="http://schema.org/Offer"{{/if}}>
            {{#if price.rrp_without_tax}}
                <span data-product-rrp-without-tax class="price price--rrp">{{price.rrp_without_tax.formatted}}</span>
            {{/if}}
            {{#if schema_org}}
                <meta itemprop="price" content="{{price.without_tax.value}}">
                <meta itemprop="priceCurrency" content="{{currency_selector.active_currency_code}}">
            {{/if}}
            <span data-product-price-without-tax class="price price--withoutTax">{{price.without_tax.formatted}}</span>
            {{#if price.with_tax}}
                <abbr title="{{lang 'products.excluding_tax'}}">{{lang 'products.price_without_tax' tax_label=price.tax_label}}</abbr>
            {{/if}}
        </div>
    {{/if}}
    {{#if page_type '===' 'product'}}
        {{#if price.saved}}
            <div class="price-section price-section--saving">
                <span class="price">
                    {{lang 'products.you_save' amount=price.saved.formatted}}
                </span>
            </div>
        {{/if}}
    {{/if}}
{{/or}}

components/common/share:
{{#if settings.add_this.buttons}}
<div class="share-heading">Share</div>
    <div class="addthis_toolbox addthis_32x32_style" addthis:url="{{url}}"
         addthis:title="{{title}}">
        <ul class="socialLinks">
            {{#each settings.add_this.buttons}}
                <li class="socialLinks-item socialLinks-item--{{service}}">
                    <a class="addthis_button_{{service}} icon icon--{{service}}" {{{annotation}}}>

                        {{#if service '===' 'facebook'}}
                        <svg>
                            <use xlink:href="#icon-facebook"/>
                        </svg>
                        {{/if}}

                        {{#if service '===' 'email'}}
                        <svg>
                            <use xlink:href="#icon-envelope"/>
                        </svg>
                        {{/if}}

                        {{#if service '===' 'print'}}
                        <svg>
                            <use xlink:href="#icon-print"/>
                        </svg>
                        {{/if}}

                        {{#if service '===' 'twitter'}}
                        <svg>
                            <use xlink:href="#icon-twitter"/>
                        </svg>
                        {{/if}}

                        {{#if service '===' 'google_plusone'}}
                            <!-- No custom icons allowed with g+ -->
                        {{/if}}

                        {{#if service '===' 'linkedin'}}
                            <svg>
                                <use xlink:href="#icon-linkedin"/>
                            </svg>
                        {{/if}}

                        {{#if service '===' 'google'}}
                            <svg>
                                <use xlink:href="#icon-google"/>
                            </svg>
                        {{/if}}

                        {{#if service '===' 'pinterest'}}
                            <svg>
                                <use xlink:href="#icon-pinterest"/>
                            </svg>
                        {{/if}}

                        {{#if service '===' 'facebook_like'}}
                            <svg>
                                <use xlink:href="#icon-facebook_like"/>
                            </svg>
                        {{/if}}
                    </a>
                </li>
            {{/each}}
        </ul>
        <script type="text/javascript"
                src="//s7.addthis.com/js/300/addthis_widget.js#pubid=ra-4e94ed470ee51e32"></script>
        <script>
            if (typeof(addthis) === "object") {
                addthis.toolbox('.addthis_toolbox');
            }
        </script>
    </div>
{{/if}}

components/products/options/set-select:
{{#if display_name '===' 'Auto Ship'}}
    {{> components/custom/fake-select-box}}
{{/if}}
<div class="form-field js-{{#replace ' ' (toLowerCase display_name)}}{{else}}js-{{toLowerCase display_name}}{{/replace}}" data-product-attribute="set-select">
    <label class="form-label form-label--alternate form-label--inlineSmall js-{{#replace ' ' (toLowerCase display_name)}}{{else}}js-{{toLowerCase display_name}}{{/replace}}" for="attribute_{{id}}">
        {{ this.display_name }}
    </label>

    <select class="form-select form-select--small" name="attribute[{{this.id}}]" id="attribute_{{id}}" {{#if required}}required{{/if}}>
        {{#each this.values}}
        <option data-product-attribute-value="{{id}}" value="{{id}}" {{#if selected}}selected{{/if}}>{{label}}</option>
        {{/each}}
    </select>
</div>
